pragma solidity 0.6.12;
pragma experimental ABIEncoderV2;

import {BoringMath, BoringMath128} from "@boringcrypto/boring-solidity/contracts/libraries/BoringMath.sol";
import {BoringBatchable} from "@boringcrypto/boring-solidity/contracts/BoringBatchable.sol";
import {BoringOwnable} from "@boringcrypto/boring-solidity/contracts/BoringOwnable.sol";
import {BoringERC20, IERC20} from "@boringcrypto/boring-solidity/contracts/libraries/BoringERC20.sol";

import {SignedSafeMath} from "./libraries/SignedSafeMath.sol";
import {IRewarder} from "./interfaces/IRewarder.sol";
import {IMiniChefV2} from "./interfaces/IMiniChefV2.sol";

/// @notice This is a modification of MiniChefV2, a SYN distribution
/// contract, that doesn't need to have minting rights for SYN token.
/// List of modifications:
/// 1. It is no longer possible to add the same LP token twice.
/// 2. Pools are updated before the new one is added.
/// 3. IRewarder is now tracking "old user LP token balance in MiniChef"
///    instead of "new user LP token balance in MiniChef"
///         [see deposit() for reasoning]
contract MiniChefV21 is BoringOwnable, BoringBatchable, IMiniChefV2 {
    using BoringMath for uint256;
    using BoringMath128 for uint128;
    using BoringERC20 for IERC20;
    using SignedSafeMath for int256;

    /// @notice Address of SYNAPSE contract.
    IERC20 public immutable SYNAPSE;

    /// @notice Info of each MCV2 pool.
    PoolInfo[] public override poolInfo;
    /// @notice Address of the LP token for each MCV2 pool.
    IERC20[] public override lpToken;
    /// @notice Address of each `IRewarder` contract in MCV2.
    IRewarder[] public override rewarder;

    /// @notice Info of each user that stakes LP tokens.
    mapping(uint256 => mapping(address => UserInfo)) public override userInfo;

    /// @dev Tokens added
    mapping(address => bool) public addedTokens;

    /// @dev Total allocation points. Must be the sum of all allocation points in all pools.
    uint256 public totalAllocPoint;

    /// @notice Total amount of unpaid SYNAPSE rewards
    uint256 public totalPendingSynapse;

    uint256 public synapsePerSecond;
    uint256 private constant ACC_SYNAPSE_PRECISION = 1e12;

    event Deposit(
        address indexed user,
        uint256 indexed pid,
        uint256 amount,
        address indexed to
    );
    event Withdraw(
        address indexed user,
        uint256 indexed pid,
        uint256 amount,
        address indexed to
    );
    event EmergencyWithdraw(
        address indexed user,
        uint256 indexed pid,
        uint256 amount,
        address indexed to
    );
    event Harvest(address indexed user, uint256 indexed pid, uint256 amount);
    event LogPoolAddition(
        uint256 indexed pid,
        uint256 allocPoint,
        IERC20 indexed lpToken,
        IRewarder indexed rewarder
    );
    event LogSetPool(
        uint256 indexed pid,
        uint256 allocPoint,
        IRewarder indexed rewarder,
        bool overwrite
    );
    event LogUpdatePool(
        uint256 indexed pid,
        uint64 lastRewardTime,
        uint256 lpSupply,
        uint256 accSynapsePerShare
    );
    event LogSynapsePerSecond(uint256 synapsePerSecond);

    /// @param _synapse The SYNAPSE token contract address.
    constructor(IERC20 _synapse) public {
        SYNAPSE = _synapse;
    }

    /// @notice Returns the number of MCV2 pools.
    function poolLength() public view override returns (uint256 pools) {
        pools = poolInfo.length;
    }

    /// @notice Add a new LP to the pool. Can only be called by the owner.
    /// Pools are updated before adding a new one, so the old rewards don't get messed up
    /// @param allocPoint AP of the new pool.
    /// @param _lpToken Address of the LP ERC-20 token.
    /// @param _rewarder Address of the rewarder delegate.
    function add(
        uint256 allocPoint,
        IERC20 _lpToken,
        IRewarder _rewarder
    ) external onlyOwner {
        require(addedTokens[address(_lpToken)] == false, "Token already added");
        for (uint256 pid = 0; pid < poolInfo.length; ++pid) {
            updatePool(pid);
        }
        totalAllocPoint = totalAllocPoint.add(allocPoint);
        lpToken.push(_lpToken);
        rewarder.push(_rewarder);

        poolInfo.push(
            PoolInfo({
                allocPoint: allocPoint.to64(),
                lastRewardTime: block.timestamp.to64(),
                accSynapsePerShare: 0
            })
        );
        addedTokens[address(_lpToken)] = true;
        emit LogPoolAddition(
            lpToken.length.sub(1),
            allocPoint,
            _lpToken,
            _rewarder
        );
    }

    /// @notice Update the given pool's SYNAPSE allocation point and `IRewarder` contract. Can only be called by the owner.
    /// @param _pid The index of the pool. See `poolInfo`.
    /// @param _allocPoint New AP of the pool.
    /// @param _rewarder Address of the rewarder delegate.
    /// @param overwrite True if _rewarder should be `set`. Otherwise `_rewarder` is ignored.
    function set(
        uint256 _pid,
        uint256 _allocPoint,
        IRewarder _rewarder,
        bool overwrite
    ) external onlyOwner {
        if (poolInfo[_pid].allocPoint != _allocPoint) {
            for (uint256 pid = 0; pid < poolInfo.length; ++pid) {
                updatePool(pid);
            }
            totalAllocPoint = totalAllocPoint
                .sub(poolInfo[_pid].allocPoint)
                .add(_allocPoint);
            poolInfo[_pid].allocPoint = _allocPoint.to64();
        }

        if (overwrite) {
            rewarder[_pid] = _rewarder;
        }
        emit LogSetPool(
            _pid,
            _allocPoint,
            overwrite ? _rewarder : rewarder[_pid],
            overwrite
        );
    }

    /// @notice Update the given pool's `IRewarder` contract. Can only be called by the owner.
    /// @param _pid The index of the pool. See `poolInfo`.
    /// @param _rewarder Address of the rewarder delegate.
    function setRewarder(uint256 _pid, IRewarder _rewarder) external onlyOwner {
        rewarder[_pid] = _rewarder;
        emit LogSetPool(_pid, poolInfo[_pid].allocPoint, _rewarder, true);
    }

    /// @notice Sets the synapse per second to be distributed. Can only be called by the owner.
    /// @param _synapsePerSecond The amount of Synapse to be distributed per second.
    function setSynapsePerSecond(uint256 _synapsePerSecond) external onlyOwner {
        synapsePerSecond = _synapsePerSecond;
        emit LogSynapsePerSecond(_synapsePerSecond);
    }

    /// @notice View function to see pending SYNAPSE on frontend.
    /// @param _pid The index of the pool. See `poolInfo`.
    /// @param _user Address of user.
    /// @return pending SYNAPSE reward for a given user.
    function pendingSynapse(uint256 _pid, address _user)
        external
        view
        returns (uint256 pending)
    {
        PoolInfo memory pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][_user];
        uint256 accSynapsePerShare = pool.accSynapsePerShare;
        uint256 lpSupply = lpToken[_pid].balanceOf(address(this));
        if (block.timestamp > pool.lastRewardTime && lpSupply != 0) {
            uint256 time = block.timestamp.sub(pool.lastRewardTime);
            uint256 synapseReward = time.mul(synapsePerSecond).mul(
                pool.allocPoint
            ) / totalAllocPoint;
            accSynapsePerShare = accSynapsePerShare.add(
                synapseReward.mul(ACC_SYNAPSE_PRECISION) / lpSupply
            );
        }
        pending = int256(
            user.amount.mul(accSynapsePerShare) / ACC_SYNAPSE_PRECISION
        ).sub(user.rewardDebt).toUInt256();
    }

    /// @notice Update reward variables for all pools. Be careful of gas spending!
    /// @param pids Pool IDs of all to be updated. Make sure to update all active pools.
    function massUpdatePools(uint256[] calldata pids) external {
        uint256 len = pids.length;
        for (uint256 i = 0; i < len; ++i) {
            updatePool(pids[i]);
        }
    }

    /// @notice Update reward variables of the given pool.
    /// @param pid The index of the pool. See `poolInfo`.
    /// @return pool Returns the pool that was updated.
    function updatePool(uint256 pid)
        public
        override
        returns (PoolInfo memory pool)
    {
        pool = poolInfo[pid];
        if (block.timestamp > pool.lastRewardTime) {
            uint256 lpSupply = lpToken[pid].balanceOf(address(this));
            if (lpSupply > 0) {
                uint256 time = block.timestamp.sub(pool.lastRewardTime);
                uint256 synapseReward = time.mul(synapsePerSecond).mul(
                    pool.allocPoint
                ) / totalAllocPoint;
                pool.accSynapsePerShare = pool.accSynapsePerShare.add(
                    (synapseReward.mul(ACC_SYNAPSE_PRECISION) / lpSupply)
                        .to128()
                );
                totalPendingSynapse = totalPendingSynapse.add(synapseReward);
            }
            pool.lastRewardTime = block.timestamp.to64();
            poolInfo[pid] = pool;
            emit LogUpdatePool(
                pid,
                pool.lastRewardTime,
                lpSupply,
                pool.accSynapsePerShare
            );
        }
    }

    /// @notice Deposit LP tokens to MCV2 for SYNAPSE allocation.
    /// @param pid The index of the pool. See `poolInfo`.
    /// @param amount LP token amount to deposit.
    /// @param to The receiver of `amount` deposit benefit.
    function deposit(
        uint256 pid,
        uint256 amount,
        address to
    ) external override {
        PoolInfo memory pool = updatePool(pid);
        UserInfo storage user = userInfo[pid][to];

        // We can't enforce IRewarder knowing everyone's current balance,
        // as it can be added to already running pool on MiniChef.

        // So instead of notifying IRewarder of the updated amount,
        // we will use the oldAmount, so IRewarder can use it to calculate
        // bonus rewards between the user interactions with the pool.

        // This way, when a user interacts with MiniChef first time after
        // the IRewarder was added, the bonus rewards will be calculated
        // properly, whether the interaction was before or after bonus
        // rewards have started dripping.
        uint256 oldAmount = user.amount;

        // Effects
        user.amount = user.amount.add(amount);
        user.rewardDebt = user.rewardDebt.add(
            int256(amount.mul(pool.accSynapsePerShare) / ACC_SYNAPSE_PRECISION)
        );

        // Interactions
        IRewarder _rewarder = rewarder[pid];
        if (address(_rewarder) != address(0)) {
            _rewarder.onSynapseReward(pid, to, to, 0, oldAmount);
        }

        lpToken[pid].safeTransferFrom(msg.sender, address(this), amount);

        emit Deposit(msg.sender, pid, amount, to);
    }

    /// @notice Withdraw LP tokens from MCV2.
    /// @param pid The index of the pool. See `poolInfo`.
    /// @param amount LP token amount to withdraw.
    /// @param to Receiver of the LP tokens.
    function withdraw(
        uint256 pid,
        uint256 amount,
        address to
    ) external override {
        PoolInfo memory pool = updatePool(pid);
        UserInfo storage user = userInfo[pid][msg.sender];
        uint256 oldAmount = user.amount;

        // Effects
        user.rewardDebt = user.rewardDebt.sub(
            int256(amount.mul(pool.accSynapsePerShare) / ACC_SYNAPSE_PRECISION)
        );
        user.amount = user.amount.sub(amount);

        // Interactions
        IRewarder _rewarder = rewarder[pid];
        if (address(_rewarder) != address(0)) {
            _rewarder.onSynapseReward(pid, msg.sender, to, 0, oldAmount);
        }

        lpToken[pid].safeTransfer(to, amount);

        emit Withdraw(msg.sender, pid, amount, to);
    }

    /// @notice Harvest proceeds for transaction sender to `to`.
    /// @param pid The index of the pool. See `poolInfo`.
    /// @param to Receiver of SYNAPSE rewards.
    function harvest(uint256 pid, address to) external override {
        PoolInfo memory pool = updatePool(pid);
        UserInfo storage user = userInfo[pid][msg.sender];
        int256 accumulatedSynapse = int256(
            user.amount.mul(pool.accSynapsePerShare) / ACC_SYNAPSE_PRECISION
        );
        uint256 _pendingSynapse = accumulatedSynapse
            .sub(user.rewardDebt)
            .toUInt256();

        // Effects
        user.rewardDebt = accumulatedSynapse;

        if (_pendingSynapse != 0) {
            totalPendingSynapse = totalPendingSynapse.sub(_pendingSynapse);
            // Interactions start here
            SYNAPSE.safeTransfer(to, _pendingSynapse);
        }

        IRewarder _rewarder = rewarder[pid];
        if (address(_rewarder) != address(0)) {
            // We need bonusFarm to recognize that bonusReward needs to be sent.
            // Rewards are only sent when _pendingSynapse > 0.
            // So we set _pendingSynapse to 1 wei, if there are no pending rewards.
            // Now in case the emissions for main pool are shut off before
            // the bonus period ends, anyone will be able to claim their bonus rewards.
            _rewarder.onSynapseReward(
                pid,
                msg.sender,
                to,
                _pendingSynapse != 0 ? _pendingSynapse : 1,
                user.amount // amount isn't modified in harvest()
            );
        }

        emit Harvest(msg.sender, pid, _pendingSynapse);
    }

    /// @notice Withdraw LP tokens from MCV2 and harvest proceeds for transaction sender to `to`.
    /// @param pid The index of the pool. See `poolInfo`.
    /// @param amount LP token amount to withdraw.
    /// @param to Receiver of the LP tokens and SYNAPSE rewards.
    function withdrawAndHarvest(
        uint256 pid,
        uint256 amount,
        address to
    ) external override {
        PoolInfo memory pool = updatePool(pid);
        UserInfo storage user = userInfo[pid][msg.sender];
        uint256 oldAmount = user.amount;
        int256 accumulatedSynapse = int256(
            user.amount.mul(pool.accSynapsePerShare) / ACC_SYNAPSE_PRECISION
        );
        uint256 _pendingSynapse = accumulatedSynapse
            .sub(user.rewardDebt)
            .toUInt256();

        // Effects
        user.rewardDebt = accumulatedSynapse.sub(
            int256(amount.mul(pool.accSynapsePerShare) / ACC_SYNAPSE_PRECISION)
        );
        user.amount = user.amount.sub(amount);

        if (_pendingSynapse != 0) {
            totalPendingSynapse = totalPendingSynapse.sub(_pendingSynapse);
            // Interactions start here
            SYNAPSE.safeTransfer(to, _pendingSynapse);
        }

        IRewarder _rewarder = rewarder[pid];
        if (address(_rewarder) != address(0)) {
            // see harvest() for _pendingSynapse explanation
            _rewarder.onSynapseReward(
                pid,
                msg.sender,
                to,
                _pendingSynapse != 0 ? _pendingSynapse : 1,
                oldAmount
            );
        }

        lpToken[pid].safeTransfer(to, amount);

        emit Withdraw(msg.sender, pid, amount, to);
        emit Harvest(msg.sender, pid, _pendingSynapse);
    }

    /// @notice Withdraw without caring about rewards. EMERGENCY ONLY.
    /// @param pid The index of the pool. See `poolInfo`.
    /// @param to Receiver of the LP tokens.
    function emergencyWithdraw(uint256 pid, address to) external override {
        UserInfo storage user = userInfo[pid][msg.sender];
        uint256 amount = user.amount;
        user.amount = 0;
        user.rewardDebt = 0;

        IRewarder _rewarder = rewarder[pid];
        if (address(_rewarder) != address(0)) {
            _rewarder.onSynapseReward(pid, msg.sender, to, 0, 0);
        }

        // Note: transfer can fail or succeed if `amount` is zero.
        lpToken[pid].safeTransfer(to, amount);
        emit EmergencyWithdraw(msg.sender, pid, amount, to);
    }
}
