    function verifySignature(
        uint256 signature,
        uint256 msgHash, address nonceTimesGeneratorAddress) internal returns (bool) {

        return this.verifySignature(signingPubKeyX, pubKeyYParity, signature, msgHash, nonceTimesGeneratorAddress);
        // TODO: the below is not working right now (despite appearing identical) fix this
